mapper.select:
SELECT

FROM T_AP_BUSINESS t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_AP_BUSINESS t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.task_name = #{taskName}


                AND t.approval_result = #{approvalResult}


                AND t.business_name = #{businessName}


                AND t.collaboration_dept = #{collaborationDept}


                AND t.header = #{header}


                AND t.start_tims = #{startTims}


                AND t.end_time = #{endTime}


                AND t.need_place = #{needPlace}


                AND t.explains = #{explains}


                AND t.funds = #{funds}


                AND t.creater = #{creater}


		order by t.update_date desc

mapper.update:
UPDATE T_AP_BUSINESS
id = #{id}, task_name = #{taskName}, approval_result = #{approvalResult}, business_name = #{businessName}, collaboration_dept = #{collaborationDept}, header = #{header}, start_tims = #{startTims}, end_time = #{endTime}, need_place = #{needPlace}, explains = #{explains}, funds = #{funds}, creater = #{creater}, update_date = SYSDATE(),
WHERE ID = #{id}

mapper.select:
select r.ID as "taskId", r.PRO_DEF_NAME as "processName", r.NAME as "taskName", u.name as "sender", t.CREATE_DATE as "time", t.START_TIMS as "startTime", t.COLLABORATION_DEPT as "collaborationDept", t.NEED_PLACE as "needPlace", t.EXPLAINS as "explains", t.FUNDS as "funds", t.END_TIME as "endTime", t.business_name AS "businessName", t.header AS "header" from V_WF_RU_TASK r inner join T_AP_BUSINESS t on t.id = r.business_id inner join T_PT_USER u on u.id = t.sender_id
(r.ASSIGNEE = #{userId} or (r.ASSIGNEE is null and r.USER_ID = #{userId}))
AND r.ID = #{taskId}
