mapper.select:
SELECT

FROM T_AP_FINANCE t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_AP_FINANCE t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.business LIKE concat('%',#{business},'%')


                AND t.money = #{money}


                AND t.purpose = #{purpose}


                AND t.task_name = #{taskName}


                AND t.approval_result = #{approvalResult}


                AND t.sender_id = #{senderId}


                AND t.creater = #{creater}


		order by t.update_date desc

mapper.update:
UPDATE T_AP_FINANCE
id = #{id}, business = #{business}, money = #{money}, purpose = #{purpose}, task_name = #{taskName}, approval_result = #{approvalResult}, sender_id = #{senderId}, creater = #{creater}, update_date = SYSDATE(),
WHERE ID = #{id}

mapper.select:
select r.ID as "taskId", r.PRO_DEF_NAME as "processName", t.business AS "business", t.money AS "money", r.NAME as "taskName", u.name as "sender", t.PURPOSE as "purpose", t.APPROVAL_RESULT as "approvalResult", t.CREATE_DATE as "time" from V_WF_RU_TASK r inner join T_AP_FINANCE t on t.id = r.business_id inner join T_PT_USER u on u.id = t.sender_id
(r.ASSIGNEE = #{userId} or (r.ASSIGNEE is null and r.USER_ID = #{userId}))
AND r.ID = #{taskId}
