mapper.select:
SELECT

FROM T_AP_LEAVE t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_AP_LEAVE t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.type = #{type}


                AND t.reason = #{reason}


                AND t.start_time = #{startTime}


                AND t.end_time = #{endTime}


                AND t.task_name = #{taskName}


                AND t.approval_result = #{approvalResult}


                AND t.creater = #{creater}


                AND t.START_TIME BETWEEN #{beginLeaveTime} AND #{endLeaveTime}


                AND u.name LIKE concat('%',#{senderName},'%')


		order by t.update_date desc

select DISTINCT t.id as "id", h.PROC_NAME as "processName", h.PROC_INST_ID as "processInstanceId", t.type as "type", u.name as "sender", t.task_name as "taskName", t.LEAVE_DAY as "leaveDay", t.CREATE_DATE as "time" from V_WF_HI_TASK h inner join T_AP_LEAVE t on t.id = h.business_id inner join T_PT_USER u on u.id = t.sender_id
h.START_USER_ID = #{userId}
AND t.type = #{leave.type}
order by t.CREATE_DATE desc

mapper.update:
UPDATE T_AP_LEAVE
id = #{id}, type = #{type}, reason = #{reason}, start_time = #{startTime}, end_time = #{endTime}, LEAVE_DAY = #{leaveDay}, task_name = #{taskName}, approval_result = #{approvalResult}, creater = #{creater}, update_date = SYSDATE(),
WHERE ID = #{id}

mapper.select:
select r.ID as "taskId", r.PRO_DEF_NAME as "processName", t.type as "type", t.type as "leaveType", r.NAME as "taskName", u.name as "sender", t.LEAVE_DAY as "leaveDay", t.START_TIME as "startTime", t.END_TIME as "endTime", t.REASON as "reason", t.CREATE_DATE as "time" from V_WF_RU_TASK r inner join T_AP_LEAVE t on t.id = r.business_id inner join T_PT_USER u on u.id = t.sender_id
(r.ASSIGNEE = #{userId} or (r.ASSIGNEE is null and r.USER_ID = #{userId}))
AND r.ID = #{taskId}
