mapper.select:
SELECT

FROM T_WF_SIMPLE t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_WF_SIMPLE t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.title LIKE concat('%',#{title},'%')


                AND t.content = #{content}


                AND t.creater = #{creater}


		order by t.update_date desc

mapper.update:
UPDATE T_WF_SIMPLE
id = #{id}, title = #{title}, content = #{content}, creater = #{creater}, task_name = #{taskName}, APPROVAL_RESULT = #{approvalResult}, update_date = SYSDATE()
WHERE ID = #{id}

mapper.select:
select r.ID as "taskId", r.PRO_DEF_NAME as "processName", t.title as "title", r.NAME as "taskName", u.name as "sender", t.CONTENT as "content", t.APPROVAL_RESULT as "approvalResult", t.CREATE_DATE as "time" from V_WF_RU_TASK r inner join T_WF_SIMPLE t on t.id = r.business_id inner join T_PT_USER u on u.id = t.creater
(r.ASSIGNEE = #{userId} or (r.ASSIGNEE is null and r.USER_ID = #{userId}))
and r.ID=#{taskId}
