mapper.select:
SELECT

FROM T_PT_USER t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_PT_USER t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.username LIKE concat('%',#{username},'%')


                AND t.name LIKE concat('%',#{name},'%')


                AND t.LAST_LOGIN_DATE = #{lastLoginDate}


                AND t.login_error_count = #{loginErrorCount}


                AND t.state = #{state}


                AND t.photo = #{photo}


                AND t.creater = #{creater}

mapper.update:
UPDATE T_PT_USER
id = #{id}, username = #{username}, password = #{password}, name = #{name}, dept_id = #{dept.id}, LAST_LOGIN_DATE = #{lastLoginDate}, login_error_count = #{loginErrorCount}, state = #{state}, photo = #{photo}, sex = #{sex}, phone = #{phone}, email = #{email}, home_place = #{homePlace}, usual_place = #{usualPlace}, is_marry = #{isMarry}, school = #{school}, is_party = #{isParty}, birthday = #{birthday}, entry_date = #{entryDate}, position = #{position}, urgent_contacts = #{urgentContacts}, creater = #{creater}, update_date = SYSDATE()
WHERE ID = #{id}
