mapper.select:
SELECT

FROM T_TRAIN t

WHERE t.DEL_FLAG = #{delFlag} and t.ID = #{id}

		SELECT

		FROM T_TRAIN t


			t.DEL_FLAG = #{delFlag}

                AND t.id = #{id}


                AND t.title LIKE concat('%',#{title},'%')


                AND u.name LIKE concat('%',#{trainer.name},'%')


                AND t.duration = #{duration}


                AND t.place = #{place}


                AND t.join_people = #{joinPeople}


                AND t.content = #{content}


                AND t.marks = #{marks}


                AND t.train_type = #{trainType}


                AND t.creater = #{creater}


		order by t.update_date desc

mapper.update:
UPDATE T_TRAIN
id = #{id}, title = #{title}, BEGIN_TIME = #{beginTime}, end_time = #{endTime}, duration = #{duration}, place = #{place}, trainer = #{trainer.id}, join_people = #{joinPeople}, content = #{content}, marks = #{marks}, train_type = #{trainType}, creater = #{creater}, update_date = SYSDATE(),
WHERE ID = #{id}
